import google from "./google.svg";
import facebook from "./facebook.svg";
import ProtectedRoute from "./ProtectedRoute";
import ProtectedLogin1 from "./ProtectedLogin";
import {
  BrowserRouter,
  Route,
  Routes,
  Link,
  // navigate,
  useNavigate,
} from "react-router-dom";
// import logo from "./reddit.png";
