function App() {
  const [currform, setcurrform] = useState("Login");
  // let navigate = useNavigate();
  const mongoose = require('mongoose')
  const DB = 'mongodb+srv://yatharth_gupta1:<GTinfo@2050>@cluster0.czyc0cx.mongodb.net/Project-0?retryWrites=true&w=majority'
  mongoose.connect(DB).then(()=>{
    console.log('connection successful')
  }).catch((err)=> console.log('no connection'));
  const [user, setUser] = useState(null);
  const toggleform = (formname) => {
    setcurrform(formname);
  };


  return (
    <BrowserRouter>
      <Routes>
        {/* <Route path="/" element={<Home />}></Route> */}
        <Route
          path="/"
          element={
            <ProtectedRoute user ={user} setUser={setUser}>
              {currform === "Login" ? (
              <Login onFormSwitch={toggleform} setUser={setUser} />
              ) : (
              <Signup onFormSwitch={toggleform} />)}
            </ProtectedRoute>
          }
        />
        {/* <Route path="/Login" element={<Login />}></Route> */}
        <Route path="/Profile_page" element={ <ProtectedLogin1 user = {user}><Profile_page /></ProtectedLogin1> }></Route>
        {/* <Route path="/Signup" element={<Signup/>}></Route> */}
      </Routes>
    </BrowserRouter>
  );
}

export default App;

