//   console.log(this.inputNode.value);
// };
function App() {
  const [currform, setcurrform] = useState("Login");
  const [backenddata, setbackenddata] = useState([{}]);
  useEffect(() => {
    fetch("/backend")
      .then((response) => response.json())
      .then((data) => {
        setbackenddata(data);
      });
  }, []);
  // let navigate = useNavigate();
  // const DB = 'mongodb+srv://yatharth_gupta1:<GTinfo@2050>@cluster0.czyc0cx.mongodb.net/?retryWrites=true&w=majority'
  // mongoose.connect(DB).then(()=>{
  //   console.log('connection successful')
  // }).catch((err)=> console.log('no connection'));
  // const { MongoClient, ServerApiVersion } = require("mongodb");
  // const uri =
  //   "mongodb+srv://yatharth_gupta1:GTinfo@2050@cluster0.czyc0cx.mongodb.net/?retryWrites=true&w=majority";
  // const client = new MongoClient(uri, {
  //   useNewUrlParser: true,
  //   useUnifiedTopology: true,
  //   serverApi: ServerApiVersion.v1,
  // });
  // client.connect((err) => {
  //   const collection = client.db("test").collection("devices");
  //   // perform actions on the collection object
  //   client.close();
  // });
  const [user, setUser] = useState(null);
  const toggleform = (formname) => {
    setcurrform(formname);
  };

  return (
    <BrowserRouter>
      {typeof backenddata.users === "undifined" ? (
        <p>loading...</p>
      ) : (
        backenddata.users.map((user, i) => <p key={i}>{user}</p>)
      )}
      <Routes>
        {/* <Route path="/" element={<Home />}></Route> */}
        <Route
          path="/"
          element={
            <ProtectedRoute user={user} setUser={setUser}>
              {currform === "Login" ? (
                <Login onFormSwitch={toggleform} setUser={setUser} />
              ) : (
                <Signup onFormSwitch={toggleform} />
              )}
            </ProtectedRoute>
          }
        />
        {/* <Route path="/Login" element={<Login />}></Route> */}
        <Route
          path="/Profile_page"
          element={
            <ProtectedLogin1 user={user}>
              <Profile_page />
            </ProtectedLogin1>
          }
        ></Route>
        {/* <Route path="/Signup" element={<Signup/>}></Route> */}
      </Routes>
    </BrowserRouter>
  );
}

export default App;

