import React from "react";
import logo from "./logo.svg";
import google from "./google.svg";
import facebook from "./facebook.svg";
import { Link, Navigate, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

const Login = (props) => {
  const [name, setname] = useState("");
  const [password, setpassword] = useState("");
  const handlechangename = (e) => {
    setname(e.target.value);
  };
  const handlechangepass = (e) => {
    setpassword(e.target.value);
  };
  let navigate = useNavigate();
  // const verify = (e) => {
  //   e.preventDefault();
  //   console.log(name, password);
  //   if (name === "admin") {
  //     if (password === "admin") {
  //       console.log("success");
  //       localStorage.setItem("login", "hello");
  //       // localStorage.re
  //       // props.setUser('xdgfd')
  //       navigate("/Profile_page");
  //     }
  //   } else {
  //     alert("Wrong ID/Password");
  //     navigate("/");
  //   }
  // };
  const Formsubmit = async (e) => {
    e.preventDefault();
    console.log({ password, name });
    await axios
      .post("http://localhost:5000/login", { password, name })
      .then((response) => {
        // setResponse(response.data)
        console.log(response.data.message === 1)
        if(response.data.message === 1)
          navigate("/Profile_page")
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  return (
    <div className="total">
      <div className="details">
        <div className="img">
          <img src={logo} alt="logo" className="image" />
        </div>
        <h3 style={{ textAlign: "center" }}>Please login to your account</h3>
        <div>
          <form className="input" onSubmit={Formsubmit}>
            {/* <fieldset> */}
            <input
              type="email"
              placeholder="Email"
              id="email_"
              required
              onChange={handlechangename}
              value={name}
              // onfocus="this.placeholder = ''"
            />
            {/* <label for="email_">Email</label> */}
            {/* </fieldset> */}
            <input
              type="password"
              placeholder="password"
              required
              onChange={handlechangepass}
              value={password}
              // onfocus="this.placeholder = ''"
            />
            <input
              type="submit"
              value="Log-in"
              className="submit"
              // onClick={verify}
            />
          </form>
        </div>
        <p style={{ textAlign: "center", opacity: "0.5" }}>Forgot Password?</p>
        <p style={{ textAlign: "center" }}>Or login using</p>
        <div className="api_login">
          <img src={google} alt="google" />
          <img src={facebook} alt="facebook" className="fb" />
        </div>
        <div className="sign-up">
          <p style={{ textAlign: "center" }}>Do not have an account?</p>
          {/* <Link to="/Signup" className="create_new">
            CREATE NEW
          </Link> */}
          <button
            className="create_new"
            onClick={() => props.onFormSwitch("Signup")}
          >
            {" "}
            CREATE NEW
          </button>
        </div>
      </div>
    </div>
  );
};
export default Login;
