import React, { useCallback } from "react";
import logo from "./logo.svg";
import { Link } from "react-router-dom";
import axios from "axios";

const Signup = (props) => {
  const data = {};
  const triggerApi = useCallback(async () => {
    // Use async await instead of chained promise
    const res = await axios.post("http://localhost:5000", data);
    console.log(res)
  });
  const post_Data = useCallback((e) => {
    e.preventDefault();
    data = {
      first_name: e.target.first_name.value,
      second_name: e.target.second_name.value,
    };
    console.log(data);
    triggerApi();
  },[triggerApi]);
  return (
    <div className="total">
      <div className="details">
        <div className="img">
          <img src={logo} alt="logo" className="image" />
        </div>
        <h3 style={{ textAlign: "center" }}>Please Enter the information</h3>
        <div>
          <form className="input" onSubmit={post_Data}>
            <input
              type="name"
              placeholder="First Name"
              name="first_name"
              required
              // onfocus="this.placeholder = ''"
            />
            <input
              type="name"
              placeholder="Second Name"
              name="second_name"
              // onfocus="this.placeholder = ''"
            />
            <input
              type="date"
              placeholder="Date Of Birth"
              name="dob"
              // onfocus="this.placeholder = ''"
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              required
              // onfocus="this.placeholder = ''"
            />
            <input
              type="password"
              placeholder="password"
              name="password"
              required
              // onfocus="this.placeholder = ''"
            />
            <input type="submit" value="Sign-up" className="submit" />
          </form>
        </div>
        <div className="sign-up">
          <p style={{ textAlign: "center" }}>Already have an account?</p>
          {/* <Link to="/Login" className="create_new">
            Login
          </Link> */}
          <button
            className="create_new"
            onClick={() => props.onFormSwitch("Login")}
          >
            LOGIN
          </button>
        </div>
      </div>
    </div>
  );
};
export default Signup;
