import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import Topbar from "../../components/topbar/Topbar";
import "./subdetails.css";
import Feed from "../../components/feed/Feed";
import CheckIcon from '@mui/icons-material/Check';
import ClearIcon from '@mui/icons-material/Clear';
const Subdetails = () => {
  const { name } = useParams();
  const [subgdata, setsubgdata] = useState({});
  const [done, setdone] = useState(false);
  const [view, setview] = useState(0);
  const [username, setusername] = useState(null);
  const [follows, setfollows] = useState(0);
  let navigate = useNavigate();
  function button_(index) {
    setview(index);
    navigate(`/subgreddiit/${name}`);
    // window.location.reload();
  }
  const [followersarray, setfollowersarray] = useState([]);
  useEffect(() => {
    const email = localStorage.getItem("hello");
    axios
      .post("http://localhost:5000/getusername", { email: email })
      .then((response) => {
        setusername(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });

  }, []);
  useEffect(() => {
    if (username) {
      axios
      .post("http://localhost:5000/findsub", { Name: name })
      .then((response) => {
        console.log(response);
        // setResponse(response.data)
        // console.log(response);
        // props.onFormSwitch("Login");
        // naviagte("/");
        // for (let i = 0; i < response?.data?.length; i++) {
        //   // if (arr[0] === 0) {
        //   // if (users.length >= response.data?.length) {
        //   //   continue;
        //   // }
        //   // console.log(response?.data[i]?.email);
        //   // if (response?.data[i]?.email === email) {
        //   //   console.log("qwerty");
        //   //   k = 1;
        //   //   continue;
        //   // }
        var temp = {
          no_of_followers: response?.data?.no_of_followers,
          no_of_posts: response?.data?.no_of_posts,
          moderator: response?.data?.moderator,
          followers: response?.data?.followers,
          blocked: response?.data?.blocked,
          left: response?.data?.left,
          request: response?.data?.request,
          Name: response?.data?.Name,
          description: response?.data?.description,
          tags: response?.data?.tags,
          banned_keywords: response?.data?.banned_keywords,
        };
        // }
        // }
        if(temp?.followers?.includes(username))
        {
          setfollows(1)
          console.log("hii there")
        }
        else if(temp?.request?.includes(username))
        {setfollows(2)}
        setfollowersarray(temp.followers);
        setsubgdata(temp);
        console.log("bsdk1 : ", temp);
        setdone(true);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
  }, [username]);

  function addinrequest() {
    // setisclicked(true)
    // const email = localStorage.getItem("hello");
    // const username1 = userdata
    // const name = subgdata[index].Name
    // console.log({userdata,name,index})
    axios
      .post("http://localhost:5000/addinrequest", { name, username })
      .then((response) => {
        console.log(response);
        // alert("added");
        // setfollowingusername((followingusername) => [
        //   ...followingusername,
        //   users[index].username,
        // ]);
        // <Navigate to="/"></Navigate>
        // navigate("/home")
        // setisclicked
        // isclicked[index] = "true"
        // console.log(isclicked[index]);
        setfollows(2)
      })
      .catch(function (error) {
        console.log(error);
      });
    // <Rightbar/>
  }

  function accept(index){
    const username1 = subgdata.request[index]
    axios
      .post("http://localhost:5000/accept", { name, username1 })
      .then((response)=>{
        console.log(response)
      })
      .catch(function (error) {
        console.log(error);
      });
      delete subgdata.request[index]
      navigate(`/subgreddiit/${name}`);
    }
    function reject(index){
      const username1 = subgdata.request[index]
    axios
      .post("http://localhost:5000/reject", { name, username1 })
      .then((response)=>{
        console.log("hvfiyah")
        console.log(response)
      })
      .catch(function (error) {
        console.log(error);
      });
      delete subgdata.request[index]
      navigate(`/subgreddiit/${name}`);
  }
    function leave(){
      // const username1 = subgdata.request[index]
    axios
      .post("http://localhost:5000/reject", { name, username })
      .then((response)=>{
        console.log("444")
        console.log(response)
      })
      .catch(function (error) {
        console.log(error);
      });
      delete subgdata.request[index]
      navigate(`/subgreddiit/${name}`);
  }

  return done ? (
    <>
      {console.log(subgdata.moderator)}
      <Topbar />
      {/* <Subsidebar /> */}
      <div style={{ display: "flex" }}>
        <div className="sidebar">
          <div className="sidebarWrapper">
            <ul className="sidebarFriendList">
              <div className="users">
                <button
                  className="sublinks"
                  onClick={() => {
                    button_(1);
                  }}
                >
                  Users
                </button>
              </div>
              {subgdata.moderator[0].email === localStorage.getItem("hello") ? (
                <>
                  <div className="users">
                    <button
                      className="sublinks"
                      onClick={() => {
                        button_(2);
                      }}
                    >
                      Joining Requests
                    </button>
                  </div>
                  <div className="users">
                    <button
                      className="sublinks"
                      onClick={() => {
                        button_(4);
                      }}
                    >
                      Reported Posts
                    </button>
                  </div>
                </>
              ) : (
                <></>
              )}
              <div className="users">
                <button
                  className="sublinks"
                  onClick={() => {
                    button_(3);
                  }}
                >
                  Stats
                </button>
              </div>
              <div className="users">
                <button
                  className="sublinks"
                  onClick={() => {
                    button_(0);
                  }}
                >
                  Posts
                </button>
              </div>
            </ul>
          </div>
        </div>
        <div className="extraview">
          {view === 3 ? (
            <div className="array">
              <p>
                <u>
                  <b>STATS</b>
                </u>
              </p>
            </div>
          ) : view === 2 ? (
            <div className="array">
              <p>
                <u>
                  <b>JOINING REQUESTS</b>
                </u>
              </p>
              {subgdata.request.map((f,i) => {
                return <span key={i}>{f}
                <ClearIcon
                  className="cancel"
                  onClick={() => {reject(i)}}
                ></ClearIcon>
                <CheckIcon
                  className="cancel"
                  onClick={() => {accept(i)}}
                ></CheckIcon>
                </span>;
              })}
            </div>
          ) : view === 1 ? (
            <div className="array">
              <p>
                <b>
                  <u>USERS</u>
                </b>
              </p>
              {subgdata.followers.map((f) => {
                return <span>{f}</span>;
              })}
            </div>
          ) : view === 4 ? (
            <div className="array">
              <p>
                <b>
                  <u>Reported Posts</u>
                </b>
              </p>
              {/* {subgdata.followers.map((f) => {
              return <span>{f}</span>;
            })} */}
            </div>
          ) : (
            <p>
              {subgdata.moderator.email === localStorage.getItem("hello")?(<></>):(<button onClick={()=>leave()}>Leave</button>)}
              {follows === 0 ? (
                <button onClick={()=>addinrequest()}>Follow</button>
              ) : follows === 1?(
                <p>following</p>
              ):follows === 2?(<p>requested</p>):(<></>)}

              <b>
                <u>Posts</u>
              </b>
            </p>
          )}
        </div>
      </div>
    </>
  ) : (
    <p>loading</p>
  );
};

export default Subdetails;
