import "./login.css";
import logo from "./logo.svg";
import google from "./google.svg";
import facebook from "./facebook.svg";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useState } from "react";
export default function Login(props) {
  // form data 
  const [name, setname] = useState("");
  const [password, setpassword] = useState("");
  const handlechangename = (e) => {
    setname(e.target.value);
  };
  const handlechangepass = (e) => {
    setpassword(e.target.value);
  };

  let navigate = useNavigate();
  const Formsubmit = async (e) => {
    e.preventDefault();
    // console.log({ password, name });
    await axios
      .post("http://localhost:5000/login", { password, name })
      .then((response) => {
        // setResponse(response.data)

        // console.log(response.data.message);
        // if (response.data.message === 1) {
        //   props.setmobile(response.data.mobile);
        //   props.setuser_name(response.data.user_name);
        //   props.setfirst_name(response.data.first_name);
        //   props.setsecond_name(response.data.second_name);
        //   props.setemail(response.data.email);
        //   props.setage(response.data.age);
        //   localStorage.setItem("hello",response.data.email);
        //   //   console.log("dfgh");
        //   props.setUser("ABCH");
        //   navigate("/Profile_page");}
        //  else if (response.data.message === -1) {
        //   alert("wrong password");
        // } else {
        //   props.setUser(null);
        //   navigate("/Profile_page");
        // }

        console.log(response.data);
        navigate("/Profile_page")
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  return (
    // <div className="login">
    //   <div className="loginWrapper">
    //     <div className="loginLeft">
    //       <h3 className="loginLogo">Lamasocial</h3>
    //       <span className="loginDesc">
    //         Connect with friends and the world around you on Lamasocial.
    //       </span>
    //     </div>
    //     <div className="loginRight">
    //       <div className="loginBox">
    //         <input placeholder="Email" className="loginInput" />
    //         <input placeholder="Password" className="loginInput" />
    //         <button className="loginButton">Log In</button>
    //         <span className="loginForgot">Forgot Password?</span>
    //         <button className="loginRegisterButton">
    //           Create a New Account
    //         </button>
    //       </div>
    //     </div>
    //   </div>
    // </div>
    <div className="total">
      <div className="details">
        <div className="img">
          <img src={logo} alt="logo" className="image" />
        </div>
        <h3 style={{ textAlign: "center" }}>Please login to your account</h3>
        <div>
          <form className="input" onSubmit={Formsubmit}>
            {/* <fieldset> */}
            <input
              type="email"
              placeholder="Email"
              id="email_"
              required
              onChange={handlechangename}
              value={name}
              // onfocus="this.placeholder = ''"
            />
            {/* <label for="email_">Email</label> */}
            {/* </fieldset> */}
            <input
              type="password"
              placeholder="password"
              required
              onChange={handlechangepass}
              value={password}
              // onfocus="this.placeholder = ''"
            />
            <input
              type="submit"
              value="Log-in"
              className="submit"
              // onClick={verify}
            />
          </form>
        </div>
        <p style={{ textAlign: "center", opacity: "0.5" }}>Forgot Password?</p>
        <p style={{ textAlign: "center" }}>Or login using</p>
        <div className="api_login">
          <img src={google} alt="google" />
          <img src={facebook} alt="facebook" className="fb" />
        </div>
        <div className="sign-up">
          <p style={{ textAlign: "center" }}>Do not have an account?</p>
          {/* <Link to="/Signup" className="create_new">
            CREATE NEW
          </Link> */}
          <button className="create_new" onClick={() => props.onFormSwitch("Signup")}>
            {" "}
            CREATE NEW
          </button>
        </div>
      </div>
    </div>
  );
}
