import "./rightbar.css";
import { Users } from "../../dummyData";
import Online from "../online/Online";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Alert, Button, Collapse } from "@mui/material";
import { useEffect } from "react";

export default function Rightbar(props) {
  const HomeRightbar = () => {
    return (
      <>
        <div className="birthdayContainer">
          <img className="birthdayImg" src="assets/gift.png" alt="" />
          <span className="birthdayText">
            <b>Pola Foster</b> and <b>3 other friends</b> have a birhday today.
          </span>
        </div>
        <img className="rightbarAd" src="assets/ad.png" alt="" />
        <h4 className="rightbarTitle">Online Friends</h4>
        <ul className="rightbarFriendList">
          {Users.map((u) => (
            <Online key={u.id} user={u} />
          ))}
        </ul>
      </>
    );
  };

  const ProfileRightbar = () => {
    const [Person_update, setPerson_update] = useState({
      first_name: props.first_name,
      second_name: props.second_name,
      age: props.age,
      contact: props.contact,
      username: props.username,
      email: props.email,
    });
    // const [alert, setalert] = useState(null);
    let navigate = useNavigate();
    const updatedb = async (e) => {
      e.preventDefault();
      console.log(Person_update);
      await axios
        .post("http://localhost:5000/update", Person_update)
        .then((response) => {
          // setResponse(response.data)
          console.log(response.data);
          console.log("yayyyyyy");
          // props.onFormSwitch("Login");
          // navigate("/Profile_page");
          // setalert("aaa");
          alert("updated");
        })
        .catch(function (error) {
          console.log(error);
        });
    };

    const onChangeHandler = (event) => {
      const { name, value } = event;
      setPerson_update((prev) => {
        return { ...prev, [name]: value };
      });
    };
    // useEffect(() => {
    // const success = (e) => {
    //   e.preventDefault();
    //   if (!alert) {
    //     return (
    //       // <Alert
    //       //   onClose={() => {
    //       //     setalert(null);
    //       //   }}
    //       //   severity="success"
    //       // >
    //       //   This is a success alert — check it out!
    //       // </Alert>
    //       <p>asdfgfhg</p>
    //     );
    //   }
    // };
    // });
    const [followers, setfollowers] = useState([]);
    function getfollowers() {
      axios
        .post("http://localhost:5000/getfollowers", { email: props.email})
        .then((response) => {
          console.log(response);
          setfollowers(response.data)
        })
        .catch(function (error) {
          console.log(error);
        });
    }
    return (
      <>
        <h3 className="rightbarTitle">
          {/* if(alert)
          {
            <Alert severity="success">This is a success alert — check it out!</Alert>
          } */}
          {/* {console.log(alert)} */}
          {/* <Alert
            onClose={() => {
              setalert(null)
            }}
            severity="success"
          >
            This is a success alert — check it out!
          </Alert> */}
          <b>
            <u>User information</u>
          </b>
        </h3>
        <form className="rightbarInfo" onSubmit={updatedb}>
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">Username:</span>
            {/* {console.log(props.username)} */}
            <input
              type="text"
              defaultValue={props.username}
              className="rightbarInfoValue"
              name="username"
              value={Person_update.username}
              onChange={(e) => onChangeHandler(e.target)}
            />
          </div>
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">Email:</span>
            {/* <input
              type="text"
              className="rightbarInfoValue"
              defaultValue={props.email}
              /> */}
            <span className="rightbarInfoValue">{props.email}</span>
          </div>
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">Contact:</span>
            <input
              type="text"
              className="rightbarInfoValue"
              name="contact"
              defaultValue={props.contact}
              value={Person_update.contact}
              onChange={(e) => onChangeHandler(e.target)}
            />
          </div>
          <div className="rightbarInfoItem">
            <span className="rightbarInfoKey">Age:</span>
            <input
              type="text"
              className="rightbarInfoValue"
              name="age"
              defaultValue={props.age}
              value={Person_update.age}
              onChange={(e) => onChangeHandler(e.target)}
            />
          </div>
          <input type="submit" className="submit" value="Update & Save" />
        </form>
        <hr />
        <h4 className="rightbarTitle">
          {" "}
          <u>
            {" "}
            <b>
              {" "}
              <button className="getfollowers" onClick={getfollowers}>
                Followers
              </button>
            </b>
          </u>
        </h4>
        <div className="followers">
          <ul>
          {followers.map((u) => (

            <div key={u.id} className="users">
              <span>
                {u.username}
              </span>
              {/* <span style={{ float: "right" }}>
                <button className="follow" onClick={() => addinfollowing(u.id)}>
                  Follow
                </button>
              </span> */}
            </div>
          ))}
          </ul>
        </div>
        <h4 className="rightbarTitle">
          {" "}
          <u>
            {" "}
            <b>Following</b>
          </u>
        </h4>
        <div className="rightbarFollowings">
          <div className="rightbarFollowing">
            <img
              src="assets/person/1.jpeg"
              alt=""
              className="rightbarFollowingImg"
            />
            <span className="rightbarFollowingName">John Carter</span>
          </div>
          <div className="rightbarFollowing">
            <img
              src="assets/person/2.jpeg"
              alt=""
              className="rightbarFollowingImg"
            />
            <span className="rightbarFollowingName">John Carter</span>
          </div>
          <div className="rightbarFollowing">
            <img
              src="assets/person/3.jpeg"
              alt=""
              className="rightbarFollowingImg"
            />
            <span className="rightbarFollowingName">John Carter</span>
          </div>
          <div className="rightbarFollowing">
            <img
              src="assets/person/4.jpeg"
              alt=""
              className="rightbarFollowingImg"
            />
            <span className="rightbarFollowingName">John Carter</span>
          </div>
          <div className="rightbarFollowing">
            <img
              src="assets/person/5.jpeg"
              alt=""
              className="rightbarFollowingImg"
            />
            <span className="rightbarFollowingName">John Carter</span>
          </div>
          <div className="rightbarFollowing">
            <img
              src="assets/person/6.jpeg"
              alt=""
              className="rightbarFollowingImg"
            />
            <span className="rightbarFollowingName">John Carter</span>
          </div>
        </div>
      </>
    );
  };
  return (
    <div className="rightbar">
      <div className="rightbarWrapper">
        {props.profile ? <ProfileRightbar /> : <HomeRightbar />}
      </div>
    </div>
  );
}
