  //       setbackenddata(data);
  //     });
  // }, []);

  // useEffect(() => {
  //   axios
  //     .get("https://jsonplaceholder.typicode.com/posts")
  //     .then((res) => setmydata(res.data));
  // }, []);
  const [user, setUser] = useState(null);
  const toggleform = (formname) => {
    setcurrform(formname);
  };

  return (
    <BrowserRouter>
      {/* {typeof backenddata.users === "undifined" ? (
        <p>loading...</p>
      ) : (
        backenddata.users.map((user, i) => <p key={i}>{user}</p>)
        // <p>done</p>
      )} */}

      {/* {mydata.map((post) => {
        const { id, title, body } = post;
        return (
          <div key={id}>
            <h2>{title}</h2>
            <p>{body}</p>
          </div>
        );
      })} */}

      <Routes>
        <Route
          path="/"
          element={
            <ProtectedRoute user={user} setUser={setUser}>
              {currform === "Login" ? (
                <Login
                  onFormSwitch={toggleform}
                  setUser={setUser}
                  setuser_name={setuser_name}
                  setmobile={setmobile}
                />
              ) : (
                <Signup onFormSwitch={toggleform} />
              )}
            </ProtectedRoute>
          }
        />
        <Route
          path="/Profile_page"
          element={
            <ProtectedLogin1 user={user}>
              <Profile_page setuser_name={setuser_name} setmobile={setmobile} />
            </ProtectedLogin1>
          }
        ></Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;

