import "./sidebar.css";
import axios from "axios";
import {
  RssFeed,
  Chat,
  PlayCircleFilledOutlined,
  Group,
  Bookmark,
  HelpOutline,
  WorkOutline,
  Event,
  School,
} from "@material-ui/icons";
import { Users } from "../../dummyData";
import CloseFriend from "../closeFriend/CloseFriend";
import { useEffect, useState } from "react";

export default function Sidebar() {
  const [users, setusers] = useState([{id:"",first_name:"",second_name:"",username:"",email:""}]);
  useEffect(() => {
    console.log("123456789");
    // const all_users_data = async (e) => {
    // e.preventDefault();
    // console.log(person);
    axios
      .post("http://localhost:5000/all_users", { message: 1 })
      .then((response) => {
        // setResponse(response.data)
        console.log("987654321");
        console.log(response.data.us);
        console.log(response.data.us[0]);
        for (let i = 0; i < 4; i++) {
          users.push({
            id:i,
            first_name: response.data.us[i].first_name,
            second_name: response.data.us[i].second_name,
            username: response.data.us[i].username,
            email: response.data.us[i].email,
          });
        }
        // console.log(users)
        // props.onFormSwitch("Login");
        // naviagte("/");
      })
      .catch(function (error) {
        console.log(error);
      });
    // };
  });
  return (
    <div className="sidebar">
      {/* {all_users_data()} */}
      <div className="sidebarWrapper">
        <ul className="sidebarList">
          <li className="sidebarListItem">
            <RssFeed className="sidebarIcon" />
            <span className="sidebarListItemText">Feed</span>
          </li>
          <li className="sidebarListItem">
            <Chat className="sidebarIcon" />
            <span className="sidebarListItemText">Chats</span>
          </li>
          <li className="sidebarListItem">
            <PlayCircleFilledOutlined className="sidebarIcon" />
            <span className="sidebarListItemText">Videos</span>
          </li>
          <li className="sidebarListItem">
            <Group className="sidebarIcon" />
            <span className="sidebarListItemText">Groups</span>
          </li>
          <li className="sidebarListItem">
            <Bookmark className="sidebarIcon" />
            <span className="sidebarListItemText">Bookmarks</span>
          </li>
          <li className="sidebarListItem">
            <HelpOutline className="sidebarIcon" />
            <span className="sidebarListItemText">Questions</span>
          </li>
          <li className="sidebarListItem">
            <WorkOutline className="sidebarIcon" />
            <span className="sidebarListItemText">Jobs</span>
          </li>
          <li className="sidebarListItem">
            <Event className="sidebarIcon" />
            <span className="sidebarListItemText">Events</span>
          </li>
          <li className="sidebarListItem">
            <School className="sidebarIcon" />
            <span className="sidebarListItemText">Courses</span>
          </li>
        </ul>
        <button className="sidebarButton">Show More</button>
        <hr className="sidebarHr" />
        <ul className="sidebarFriendList">
          {/* axios.post('http://localhost:5000/signup', person)
      .then((response) => {
        // setResponse(response.data)
        console.log(response.data)
        props.onFormSwitch("Login");
        naviagte("/")
      })
      .catch(function (error) {
        console.log(error);
      }); */}
          {/* axios.post('http://localhost:5000/all_users',) */}
          {users.map((u) => (
            <li key={u.id} user={u}>
              {u.first_name} {u.second_name}
              {/* console.log({u.id}); */}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
