import "./profile.css";
import Topbar from "../../components/topbar/Topbar";
import Sidebar from "../../components/sidebar/Sidebar";
import Feed from "../../components/feed/Feed";
import Rightbar from "../../components/rightbar/Rightbar";
import { useState } from "react";

export default function Profile(props) {
  const [followerusername, setfollowerusername] = useState([]);
  const [followingusername, setfollowingusername] = useState([]);
  const [followingemail, setfollowingemail] = useState([]);
  const [followersemail, setfollowersemail] = useState([]);

  // const [fetched, setfetched] = useState(false);
  console.log("zxcvbnm")

  const [fetched, setfetched] = useState(false);
    // const [count,setcount] = useState(0);

  useEffect(() => {
    async function fetchData() {
      const res = await axios.post("http://localhost:5000/getfollowers", {
        email: props.email,
      });
      try {
        console.log(res);
        setfollowersemail(res.data);
      } catch (err) {
        console.log(err);
      }
      const res1 = await axios.post("http://localhost:5000/getfollowing", {
        email: props.email,
      });
      try {
        console.log(res1);
        setfollowingemail(res1.data);
      } catch (err) {
        console.log(err);
      }
      setfetched(!fetched);
    }
    fetchData();
    console.log("ertyjnbvcfghjn")
  }, []);

  useEffect(() => {
    async function fetchData1() {
      if (followersemail) {
        console.log(followersemail);
        const res2 = await Promise.all(
          followersemail.map(async (f) => {
            const res3 = await axios.post(
              "http://localhost:5000/getusername",
              {
                email: f,
              }
            );
            try {
              console.log(res3);
              const abcd = props.followerusername
              props.setfollowerusername((abcd) => [
                ...abcd,
                res3.data,
              ]);
            } catch (err) {
              console.log(err);
            }
          })
        );
        console.log(res2);
        // setfollowerusername(res2);
        // setload(true);
      }
    }
    fetchData1();
  }, [fetched]);

  useEffect(() => {
    async function fetchData1() {
      console.log(fetched)
      if (followingemail) {
        console.log(followingemail);
        const res2 = await Promise.all(
          followingemail.map(async (f) => {
            const res3 = await axios.post(
              "http://localhost:5000/getusername",
              {
                email: f,
              }
            );
            try {
              console.log(res3);
              const abc = props.followingusername
              props.setfollowingusername((abc) => [
                ...abc,
                res3.data,
              ]);
            } catch (err) {
              console.log(err);
            }
          })
        );
        console.log(res2);
        // setfollowerusername(res2);
        // setload(true);
      }
    }
    fetchData1();
  }, [fetched]);
  return (
    <>
      <Topbar />
      <div className="profile">
        <Sidebar email = {props?.userdata?.email} followingusername = {followingusername} setfollowingusername = {setfollowingusername}/>
        <div className="profileRight">
          <div className="profileRightTop">
            <div className="profileCover">
              <img
                className="profileCoverImg"
                src="assets/post/3.jpeg"
                alt=""
              />
              <img
                className="profileUserImg"
                src="assets/person/7.jpeg"
                alt=""
              />
            </div>
            <div className="profileInfo">
              <h4 className="profileInfoName">
                {props?.userdata?.first_name} {props?.userdata?.second_name} 
              </h4>
              <span className="profileInfoDesc">Hello my friends!</span>
            </div>
          </div>
          <div className="profileRightBottom">
            <Feed />
            {/* {console.log(props?.userdata?.username)} */}
            <Rightbar
              profile
              username={props?.userdata?.username}
              first_name={props?.userdata?.first_name}
              second_name={props?.userdata?.second_name}
              age={props?.userdata?.age}
              contact={props?.userdata?.contact}
              email={props?.userdata?.email}
              followerusername = {followerusername}
              followeremail = {followeremail}
              followingusername = {followingusername}
              followingemail = {followingemail}
              // setfollowerusername = {setfollowerusername}
              // setfollowingusername = {setfollowingusername}

              // fetched = {fetched}
              // setfetched = {setfetched}
            />
          </div>
        </div>
      </div>
    </>
  );
}
