import "./rightbar.css";
import { Users } from "../../dummyData";
import Online from "../online/Online";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Alert, Button, Collapse } from "@mui/material";
import { useEffect } from "react";
import { set } from "mongoose";
import CancelIcon from "@mui/icons-material/Cancel";

export default function Rightbar(
  props
  // followerusername,
  // setfollowerusername,
  // followingusername,
  // setfollowingusername,
) {
  const HomeRightbar = () => {
    return (
      <>
        <div className="birthdayContainer">
          <img className="birthdayImg" src="assets/gift.png" alt="" />
          <span className="birthdayText">
            <b>Pola Foster</b> and <b>3 other friends</b> have a birhday today.
          </span>
        </div>
        <img className="rightbarAd" src="assets/ad.png" alt="" />
        <h4 className="rightbarTitle">Online Friends</h4>
        <ul className="rightbarFriendList">
          {Users.map((u) => (
            <Online key={u.id} user={u} />
          ))}
        </ul>
      </>
    );
  };

  const ProfileRightbar = () => {
    const [Person_update, setPerson_update] = useState({
      first_name: props.first_name,
      second_name: props.second_name,
      age: props.age,
      contact: props.contact,
      username: props.username,
      email: props.email,
    });
    // const [alert, setalert] = useState(null);
    let navigate = useNavigate();
    const updatedb = (e) => {
      e.preventDefault();
      console.log(Person_update);
      axios
        .post("http://localhost:5000/update", { user: Person_update })
        .then((response) => {
          // setResponse(response.data)
          if (response.data.message === 1) {
            console.log(response.data);
            console.log("yayyyyyy");
            // props.onFormSwitch("Login");
            // navigate("/Profile_page");
            // setalert("aaa");
            alert("updated");
          } else {
            alert("err");
          }
        })
        .catch(function (error) {
          console.log(error);
        });
    };

    const onChangeHandler = (event) => {
      const { name, value } = event;
      setPerson_update((prev) => {
        return { ...prev, [name]: value };
      });
    };


    const [load, setload] = useState(false);
    const [followersemail, setfollowersemail] = useState([]);
    // const [followerusername, setfollowerusername] = useState([]);
    const [followingemail, setfollowingemail] = useState([]);
    // const [followingusername, setfollowingusername] = useState([]);
    const [fetched, setfetched] = useState(false);
    useEffect(() => {
      async function fetchData() {
        const res = await axios.post("http://localhost:5000/getfollowers", {
          email: props.email,
        });
        try {
          console.log(res);
          setfollowersemail(res.data);
        } catch (err) {
          console.log(err);
        }
        const res1 = await axios.post("http://localhost:5000/getfollowing", {
          email: props.email,
        });
        try {
          console.log(res1);
          setfollowingemail(res1.data);
        } catch (err) {
          console.log(err);
        }
        // if(!fetched)
        setfetched(!fetched);
      }
      fetchData();
    }, []);
    useEffect(() => {
      async function fetchData1() {
        if (followersemail) {
          console.log(followersemail);
          const res2 = await Promise.all(
            followersemail.map(async (f) => {
              const res3 = await axios.post(
                "http://localhost:5000/getusername",
                {
                  email: f,
                }
              );
              try {
                console.log(res3);
                const abcd = props.followerusername
                props.setfollowerusername((abcd) => [
                  ...abcd,
                  res3.data,
                ]);
              } catch (err) {
                console.log(err);
              }
            })
          );
          console.log(res2);
          // setfollowerusername(res2);
          setload(true);
        }
      }
      fetchData1();
    }, [fetched]);

    const deleteitem = (deleteuserid) => {
      console.log("hiii");
      console.log(followingemail[deleteuserid]);
      const email = props.email;
      const email1 = followingemail[deleteuserid];

      axios
        .post("http://localhost:5000/delete", {
          email,
          email1,
        })
        .then((response) => {
          // setResponse(response.data)
          console.log(response?.data);
          console.log("yayyyyyy");
          // props.onFormSwitch("Login");
          // navigate("/Profile_page");
          // setalert("aaa");
          // setfetched(false)
          delete props.followingusername[deleteuserid];
          navigate("/Profile_page");
        })
        .catch(function (error) {
          console.log(error);
        });
      // <Sidebar/>
      // try{
      //   console.log(res)
      //   // setfetched(false)
      // }
      // catch(err)
      // {
      //   console.log(err)
      // }
    };
    const deleteitem1 = (deleteuserid) => {
      console.log("hiii");
      console.log(followersemail[deleteuserid]);
      const email1 = props.email;
      const email = followersemail[deleteuserid];

      axios
        .post("http://localhost:5000/delete", {
          email1,
          email,
        })
        .then((response) => {
          // setResponse(response.data)
          console.log(response?.data);
          console.log("yayyyyyy1");
          // props.onFormSwitch("Login");
          // navigate("/Profile_page");
          // setalert("aaa");
          // setfetched(false)
          delete props.followerusername[deleteuserid];
          navigate("/Profile_page");
        })
        .catch(function (error) {
          console.log(error);
        });
      // try{
      //   console.log(res)
      //   // setfetched(false)
      // }
      // catch(err)
      // {
      //   console.log(err)
      // }
    };
    useEffect(() => {
      async function fetchData2() {
        console.log("3456789876543234567")
