import logo from "./logo.svg";
import google from "./google.svg";
import facebook from "./facebook.svg";
import { BrowserRouter, Route, Routes, Link } from "react-router-dom";
// import logo from "./reddit.png";
import Signup from "./signup";
import "./App.css";

function App() {
  return (
    <BrowserRouter>
      <Routes>
        <div className="total">
          <div className="details">
            <div className="img">
              <img src={logo} alt="logo" className="image" />
            </div>
            <h3 style={{ textAlign: "center" }}>
              Please login to your account
            </h3>
            <div>
              <form className="input">
                <input
                  type="email"
                  placeholder="Email"
                  required
                  // onfocus="this.placeholder = ''"
                />
                <input
                  type="password"
                  placeholder="password"
                  required
                  // onfocus="this.placeholder = ''"
                />
                <input type="submit" value="Log-in" className="submit" />
              </form>
            </div>
            <p style={{ textAlign: "center", opacity: "0.5" }}>
              Forgot Password?
            </p>
            <p style={{ textAlign: "center" }}>Or login using</p>
            <div className="api_login">
              <img src={google} alt="google" />
              <img src={facebook} alt="facebook" className="fb" />
            </div>
          </div>
        </div>
      </Routes>
      <div className="sign-up">
        <p>Don't have an account?</p>
        {/* <button className="create_new" onClick={<Link to="/signup"></Link>}>Create New</button> */}
        {/* <Switch>
              <Route exact path="/signup">
                <Signup />
                </Route>
              </Switch> */}
        {/* <Route exact path="/signup" /> */}
        {/* <Link to="/signup">CreateNew</Link> */}
        <Routes>
          <Link to="/signup">signup</Link>

          {/* <Route path="/signup" element={<Signup />}></Route> */}
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;

