function App() {
  const [currform, setcurrform] = useState("Login");
  // let navigate = useNavigate();
  const [user, setUser] = useState(null);
  const toggleform = (formname) => {
    setcurrform(formname);
  };
  useEffect(()=>{
    if (localStorage.getItem("login") === "hello") {
      setUser("sdgsg");
    }
  })

  return (
    <BrowserRouter>
      <Routes>
        {/* <Route path="/" element={<Home />}></Route> */}
        <Route
          path="/"
          element={
            <ProtectedRoute user ={user}>
              {currform === "Login" ? (
              <Login onFormSwitch={toggleform} setUser={setUser} />
              ) : (
              <Signup onFormSwitch={toggleform} />)}
            </ProtectedRoute>
          }
        />
        {/* <Route path="/Login" element={<Login />}></Route> */}
        <Route path="/dashboard" element={ <ProtectedLogin1 user = {user}><Dashboard /></ProtectedLogin1> }></Route>
        {/* <Route path="/Signup" element={<Signup/>}></Route> */}
      </Routes>
    </BrowserRouter>
  );
}

export default App;

