import pic from "./dp.svg"
import logo from "./logo1.svg"
import { Navigate,useNavigate } from "react-router-dom"
import { useState } from "react"
const Profile_page = ({user,setUser}) => {
    let navigate = useNavigate()
    const logout=()=>{
        localStorage.removeItem("login")
        // setUser(null)
        // if(!user)
        // return <Navigate to="/"></Navigate>
        navigate("/")
        // window.location.reload();
        // <Navigate to="/"></Navigate>
    }
    const [open,setopen] = useState(false)
    const handledrop  = ()=>{
        setopen(!open)
    }
 return (

    <div className="dp">
        <nav className="navbar">
        <div className="logo">
            <img src={logo} alt="reddit" className="image"/>
        </div>
        <div className="items">
            <ul>Home</ul>
            <ul>About</ul>
            <ul>Services</ul>
            <ul>Contact</ul>
            <ul onClick={logout}>Logout</ul>
        </div>
        </nav>
        <img src={pic} alt="dp" className="img_dp"/>
        <h3 style={{textALign:"center"}}><u><b>Profile Picture</b></u></h3>
        <div className="friends">
            <div className="dropdownfollowers">
                <button className="followers" onClick={handledrop}></button>
                {open ? (
                    <ul> 
                        <li>yatharth</li>
                        <li>vansh</li>
                    </ul>
                ):null}
            </div>
        </div>
    </div>
 );
}
export default Profile_page;
