import React from "react";
import logo from "./logo.svg";
import { Link } from "react-router-dom";
import axios from "axios";
import { useState } from "react";
const Signup = (props) => {

  // interface response {
  //   response: string | "No Response"
  // }
    const inputStyle = {border: "1px solid black", height: 75, "padding": 10}
    const [person, setPerson] = useState([{first_name:"yatharth",
    second_name:"gupta"}])
    const [response, setResponse] = useState(null)

    const sumbitForm = (e) => {
      e.preventDefault()
      axios.post('http://localhost:5000/', person)
      .then((response) => {
        setResponse(response.data)
        console.log(response.data)
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    const onChangeHandler = (event) => {
      const {name, value} = event
        setPerson((prev) => {
          return {...prev, [name]: value}
        })
    }
  return (
    <div className="total">
      <div className="details">
        <div className="img">
          <img src={logo} alt="logo" className="image" />
        </div>
        <h3 style={{ textAlign: "center" }}>Please Enter the information</h3>
        <div>
          <form className="input" onSubmit={sumbitForm}>
            <input
              type="name"
              placeholder="First Name"
              name="first_name"
              value={person.first_name} onChange={(e) => onChangeHandler(e.target)}
              required
              // onfocus="this.placeholder = ''"
              />
            <input
              type="name"
              placeholder="Second Name"
              name="second_name"
              value={person.second_name} onChange={(e) => onChangeHandler(e.target)}
              // onfocus="this.placeholder = ''"
            />
            <input
              type="date"
              placeholder="Date Of Birth"
              name="dob"
              // onfocus="this.placeholder = ''"
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              required
              // onfocus="this.placeholder = ''"
            />
            <input
              type="password"
              placeholder="password"
              name="password"
              required
              // onfocus="this.placeholder = ''"
            />
            <input type="submit" value="Sign-up" className="submit" />
          </form>
        </div>
        <div className="sign-up">
          <p style={{ textAlign: "center" }}>Already have an account?</p>
          {/* <Link to="/Login" className="create_new">
            Login
          </Link> */}
          <button
            className="create_new"
            onClick={() => props.onFormSwitch("Login")}
          >
            LOGIN
          </button>
        </div>
      </div>
    </div>
  );
};
export default Signup;
