import Home from "./pages/home/Home";
import Login from "./pages/login/Login";
import Profile from "./pages/profile/Profile";
import Register from "./pages/register/Register";
import ProtectedRoute from "./ProtectedRoute";
import ProtectedLogin1 from "./ProtectedLogin";
import ProtectedLogin from "./ProtectedLogin_1";
import { useState } from "react";
import {
  BrowserRouter,
  Route,
  Routes,
  Link,
  // navigate,
  useNavigate,
} from "react-router-dom";
function App() {
  //login-signup toggle
  const [currform, setcurrform] = useState("Login");
  const toggleform = (formname) => {
    setcurrform(formname);
  };

  // to check user presence
  const [user, setUser] = useState(null);

  //userdata
  const [username, setusername] = useState("");
  const [first_name, setfirst_name] = useState("");
  const [second_name, setsecond_name] = useState(null);
  const [contact, setcontact] = useState(null);
  const [email, setemail] = useState(null);
  const [age, setage] = useState(null);

  const [userdata,setuserdata] = useState(null)
  const[followers,setfollowers] = useState([]);
  const[following,setfollowing] = useState([]);
  return (
    <BrowserRouter>
      <Routes>
        {/* <Route path="/" element={<Login />} /> */}
        <Route
          path="/"
          element={
            <ProtectedRoute user={user} setUser={setUser}>
              {currform === "Login" ? (
                <Login
                  onFormSwitch={toggleform}
                  setUser={setUser}
                  setage={setage}
                  setcontact={setcontact}
                  setemail={setemail}
                  setfirst_name={setfirst_name}
                  setsecond_name={setsecond_name}
                  setusername={setusername}
                />
              ) : (
                <Register onFormSwitch={toggleform} />
              )}
            </ProtectedRoute>
          }
        />
        <Route
          path="/home"
          element={
            // <ProtectedLogin user={user}>
            <Home />
            // </ProtectedLogin>
          }
        />
        <Route
          path="/Profile_page"
          element={
            <ProtectedLogin1
              user={user}
              setfirst_name={setfirst_name}
              setsecond_name={setsecond_name}
              setusername={setusername}
              setage={setage}
              setemail={setemail}
              setcontact={setcontact}
            >
              <Profile
                age={age}
                contact={contact}
                email={email}
                first_name={first_name}
                second_name={second_name}
                username={username}
                followers = {followers}
                setfollowers = {setfollowers}
                following = {following}
                setfollowing = {setfollowing}
              />
            </ProtectedLogin1>
          }
        />
        {/* <Route
          path="/"
          element={
            <ProtectedRoute user={user} setUser={setUser}>
              {currform === "Login" ? (
                <Login
                  onFormSwitch={toggleform}
                  setUser={setUser}
                  setusername={setusername}
                  setcontact={setcontact}
                  setfirst_name={setfirst_name}
                  setsecond_name={setsecond_name}
                  setemail={setemail}
                />
              ) : (
                <Signup onFormSwitch={toggleform} />
              )}
            </ProtectedRoute>
          }
        />
        <Route
          path="/Profile_page"
          element={
            <ProtectedLogin1 user={user}>
              <Profile_page
                username={username}
                contact={contact}
                first_name={first_name}
                second_name={second_name}
                email={email}
              />
            </ProtectedLogin1>
          }
        /> */}
      </Routes>
    </BrowserRouter>
  );
}

export default App;
