import Home from "./pages/home/Home";
import Login from "./pages/login/Login";
import Profile from "./pages/profile/Profile";
import Register from "./pages/register/Register";
import ProtectedRoute from "./ProtectedRoute";
import ProtectedLogin1 from "./ProtectedLogin";
import ProtectedLogin from "./ProtectedLogin_1";
import { useState } from "react";
import {
  BrowserRouter,
  Route,
  Routes,
  Link,
  // navigate,
  useNavigate,
} from "react-router-dom";
function App() {
  //login-signup toggle
  const [currform, setcurrform] = useState("Login");
  const toggleform = (formname) => {
    setcurrform(formname);
  };

  // to check user presence
  const [user, setUser] = useState(null);

  //userdata
  const [user_name, setuser_name] = useState("");
  const [first_name, setfirst_name] = useState("");
  const [second_name, setsecond_name] = useState(null);
  const [mobile, setmobile] = useState(null);
  const [email, setemail] = useState(null);
  const [age, setage] = useState(null);

  return (
    <BrowserRouter>
      <Routes>
        {/* <Route path="/" element={<Login />} /> */}
        <Route
          path="/"
          element={
            <ProtectedRoute user={user} setUser={setUser} email={email}>
              {currform === "Login" ? (
                <Login
                  onFormSwitch={toggleform}
                  setUser={setUser}
                  setage={setage}
                  setmobile={setmobile}
                  setemail={setemail}
                  setfirst_name={setfirst_name}
                  setsecond_name={setsecond_name}
                  setuser_name={setuser_name}
                />
              ) : (
                <Register onFormSwitch={toggleform} />
              )}
            </ProtectedRoute>
          }
        />
        <Route
          path="/home"
          element={
            <ProtectedLogin user={user}>
              <Home />
            </ProtectedLogin>
          }
        />
        <Route
          path="/Profile_page"
          element={
            <ProtectedLogin1 user={user} setfirst_name={setfirst_name} setsecond_name={setsecond_name}>
              <Profile
                age={age}
                mobile={mobile}
                email={email}
                first_name={first_name}
                second_name={second_name}
                user_name={user_name}
              />
            </ProtectedLogin1>
          }
        />
        {/* <Route
          path="/"
          element={
            <ProtectedRoute user={user} setUser={setUser}>
              {currform === "Login" ? (
                <Login
                  onFormSwitch={toggleform}
                  setUser={setUser}
                  setuser_name={setuser_name}
                  setmobile={setmobile}
                  setfirst_name={setfirst_name}
                  setsecond_name={setsecond_name}
                  setemail={setemail}
                />
              ) : (
                <Signup onFormSwitch={toggleform} />
              )}
            </ProtectedRoute>
          }
        />
        <Route
          path="/Profile_page"
          element={
            <ProtectedLogin1 user={user}>
              <Profile_page
                user_name={user_name}
                mobile={mobile}
                first_name={first_name}
                second_name={second_name}
                email={email}
              />
            </ProtectedLogin1>
          }
        /> */}
      </Routes>
    </BrowserRouter>
  );
}

export default App;
